import React, { useState } from "react";
import styled from "styled-components";

export const SignUpForm = () => {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    age: "",
    password: "",
    confirmPassword: "",
  });
  const [nameError, setNameError] = useState(""); // 이름 입력 에러 상태
  const [emailError, setEmailError] = useState(""); // 이메일 입력 에러 상태

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });

    if (!isFormValid()) {
      return;
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // 유효성 검사를 진행합니다.
    if (!isFormValid()) {
      return;
    }
    // 여기에 제출 로직을 추가하세요
    console.log("Form submitted!");
  };

  // 입력 유효성을 검사하는 함수
  const isFormValid = () => {
    let valid = true;

    if (formData.name.trim() === "") {
      setNameError("이름을 입력해주세요!");
      valid = false;
    } else {
      setNameError("");
    }

    if (formData.email.trim() === "" || !formData.email.includes("@")) {
      setEmailError("이메일을 입력해주세요!");
      valid = false;
    } else {
      setEmailError("");
    }

    return valid;
  };

  return (
    <SignupContainer>
      <div>회원가입 페이지</div>
      <div className="inputDiv">
        <input
          type="text"
          name="name"
          value={formData.name}
          onChange={handleChange}
          placeholder="이름을 입력해주세요."
        />
        <span className="error">{nameError}</span>
        <input
          type="text"
          name="email"
          value={formData.email}
          onChange={handleChange}
          placeholder="이메일을 입력해주세요."
        />
        <span className="error">{emailError}</span>
        <input
          type="number"
          name="age"
          value={formData.age}
          onChange={handleChange}
          placeholder="나이를 입력해주세요."
        />
        <input
          type="password"
          name="password"
          value={formData.password}
          onChange={handleChange}
          placeholder="비밀번호를 입력해주세요."
        />
        <input
          type="password"
          name="confirmPassword"
          value={formData.confirmPassword}
          onChange={handleChange}
          placeholder="비밀번호 확인"
        />
      </div>
      <button onClick={handleSubmit}>제출하기</button>
      <div className="movePage">
        <span>이미 아이디가 있으신가요?</span>
        <span>로그인 페이지로 이동하기</span>
      </div>
    </SignupContainer>
  );
};

const SignupContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 80px 0;
  gap: 45px;

  .inputDiv {
    display: flex;
    flex-direction: column;
    gap: 20px;
  }

  .inputDiv > div {
    display: flex;
    flex-direction: column;
    justify-content: center;
  }

  .error {
    color: red; /* 메시지 색상 */
    font-size: 12px;
    padding-left: 15px;
  }

  input {
    width: 400px; /* 입력 필드 너비 */
    height: 40px;
    padding-left: 15px;
    border-radius: 20px; /* 입력 필드 테두리 반지름 */
  }

  button {
    width: 420px; /* 버튼 너비 */
    height: 50px;
    font-weight: bold;
    font-size: 16px;
    border-radius: 25px; /* 버튼 테두리 반지름 */
    cursor: pointer;
  }

  .movePage {
    display: flex;
    gap: 30px;
  }
`;
