import { useEffect, useState } from "react";
import { useLocation } from "react-router-dom";
import axios from "axios";
import styled from "styled-components";

const NO_IMAGE =
  "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSz7ztleRwzXhFdiwBYqZ8cib9RvEsukVVUS3niN1YQ&s";

const MovieDetailContainer = styled.div`
  background-image: url(${(props) => props.backdropUrl});
  background-repeat: no-repeat;
  background-size: cover;
  background-color: rgba(32, 35, 75, 0.9);
  background-blend-mode: multiply;
  padding: 200px 250px;
  display: flex;

  @media (max-width: 900px) {
    padding: 80px 180px;
    flex-direction: column;
    background-image: none;
    background-color: hsl(233, 43%, 16%);
  }

  @media (max-width: 750px) {
    flex-direction: column;
    padding: 80px 100px;
    background-image: none;
    background-color: hsl(233, 43%, 16%);
  }

  @media (max-width: 600px) {
    flex-direction: column;
    padding: 80px 20px;
    background-image: none;
    background-color: hsl(233, 43%, 16%);
  }

  .backgroundImg {
    background-color: rgb(255, 255, 255, 0.5);
  }

  .movie_thumnail {
    img {
      width: 370px;
      height: 490px;

      @media (max-width: 600px) {
        width: 250px;
        height: 350px;
      }
    }
  }

  .movie_detail {
    width: 600px;
    display: flex;
    flex-direction: column;
    gap: 35px;
    padding: 100px 0 0 100px;

    @media (max-width: 900px) {
      padding: 20px 0 0 0;
      width: 500px;
    }

    @media (max-width: 600px) {
      padding: 20px 0 0 0;
      width: 400px;
      gap: 20px;
    }

    .title {
      font-weight: bold;
      font-size: 30px;

      @media (max-width: 900px) {
        font-size: 24px;
      }

      @media (max-width: 600px) {
        font-size: 20px;
      }
    }

    .detail {
      font-size: 15px;

      @media (max-width: 900px) {
        font-size: 14px;
      }

      @media (max-width: 600px) {
        font-size: 12px;
      }
    }
  }
`;

const CastContainer = styled.div`
  margin-top: 20px;
  display: flex;
  flex-wrap: wrap;
  gap: 20px;

  .cast-card {
    display: flex;
    flex-direction: column;
    align-items: center;
    width: 150px;

    img {
      width: 150px;
      height: 225px;
      object-fit: cover;
    }

    .name {
      margin-top: 10px;
      font-weight: bold;
      text-align: center;
    }

    .character {
      text-align: center;
    }
  }
`;

export const MovieDetailPage = () => {
  const location = useLocation();
  const state = location.state || {};
  const { id } = state;

  const [movieDetails, setMovieDetails] = useState(state);
  const [cast, setCast] = useState([]);
  const [crew, setCrew] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    if (!id) {
      setLoading(false);
      return;
    }

    const fetchMovieDetails = async () => {
      try {
        const movieResponse = await axios.get(
          `https://api.themoviedb.org/3/movie/${id}?api_key=YOUR_API_KEY&language=ko-KR`
        );
        const creditsResponse = await axios.get(
          `https://api.themoviedb.org/3/movie/${id}/credits?api_key=YOUR_API_KEY&language=ko-KR`
        );

        setMovieDetails(movieResponse.data);
        setCast(creditsResponse.data.cast);
        setCrew(creditsResponse.data.crew);
        setLoading(false);
      } catch (error) {
        console.error("Error fetching movie details:", error);
      }
    };

    fetchMovieDetails();
  }, [id]);

  if (loading) {
    return <p>Loading...</p>;
  }

  const {
    original_title,
    backdrop_path,
    poster_path,
    vote_average,
    release_date,
    overview,
  } = movieDetails;

  const movie_rating = vote_average && "⭐️".repeat(Math.floor(vote_average));
  const summary = overview
    ? overview
    : "TMDB에서 제공하는 API에 상세 줄거리 정보가 없습니다.";

  return (
    <MovieDetailContainer
      backdropUrl={`http://image.tmdb.org/t/p/w500/${backdrop_path}`}
    >
      <div className="movie_thumnail">
        <img
          src={
            poster_path
              ? `http://image.tmdb.org/t/p/w500/${poster_path}`
              : NO_IMAGE
          }
          alt="영화 썸네일"
        />
      </div>
      <div className="movie_detail">
        <div className="title">{original_title}</div>
        <div>평점 {movie_rating}</div>
        <div>개봉일 {release_date}</div>
        <div>줄거리</div>
        <div className="detail">{summary}</div>
      </div>
      <CastContainer>
        {cast.map((member) => (
          <div key={member.cast_id} className="cast-card">
            <img
              src={
                member.profile_path
                  ? `http://image.tmdb.org/t/p/w500/${member.profile_path}`
                  : NO_IMAGE
              }
              alt={member.name}
            />
            <div className="name">{member.name}</div>
            <div className="character">{member.character}</div>
          </div>
        ))}
      </CastContainer>
      <CastContainer>
        {crew
          .filter((member) => member.job === "Director")
          .map((member) => (
            <div key={member.credit_id} className="cast-card">
              <img
                src={
                  member.profile_path
                    ? `http://image.tmdb.org/t/p/w500/${member.profile_path}`
                    : NO_IMAGE
                }
                alt={member.name}
              />
              <div className="name">{member.name}</div>
              <div className="character">{member.job}</div>
            </div>
          ))}
      </CastContainer>
    </MovieDetailContainer>
  );
};
