import { useLoaderData } from "react-router-dom";
import styled from "styled-components";

export const MovieDetailPage = () => {
  const state = useLoaderData.state;
  const { title, backdrop_path, poster_path, rate, release_date, overview } =
    state;

  return (
    <MoveDetailContainer>
      <div className="movie_thumnail">
        <img
          src={`https://i.namu.wiki/i/ghGkwrTfWQFYOUx4T7Js97ZSeAr5LT1freOGRjZC-AR45yKDG-fHqznMYVNp1k5JqLrBPGNUN_mpbk2YKKWRka1vQRGlTVsS0V7UjFha0As2n95mZRUkLJpJHsWygTzPWI8EnCawZBaZgQDHChnufQ.webp`}
          alt="영화 썸네일"
        />
      </div>
      <div className="movie_detail">
        <div className="title">Dune: Part Two</div>
        <div>평점 ⭐️⭐️⭐️⭐️⭐️⭐️⭐️⭐️</div>
        <div>개봉일 2024-02-27</div>
        <div>줄거리</div>
        <div className="detail">
          황제의 모략으로 멸문한 가문의 유일한 후계자 폴(티모시 샬라메). 어머니
          레이디 제시카(레베카 퍼거슨)와 간신히 목숨만 부지한 채 사막으로
          도망친다. 그곳에서 만난 반란군들과 숨어 지내다 그들과 함께 황제의 모든
          것을 파괴할 전투를 준비한다. 한편 반란군들의 기세가 높아질수록
          불안해진 황제와 귀족 가문은 잔혹한 암살자 페이드 로타(오스틴 버틀러)를
          보내 반란군을 몰살하려 하는데… 2월, 운명의 반격이 시작된다!
        </div>
      </div>
    </MoveDetailContainer>
  );
};

export const MoveDetailContainer = styled.div`
  padding: 200px 250px;
  display: flex;

  .backgroundImg {
    background-color: rgb(255, 255, 255, 0.5);
  }

  .movie_thumnail {
    img {
      width: 370px;
      height: 550px;
    }
  }

  .movie_detail {
    width: 800px;
    display: flex;
    flex-direction: column;
    gap: 35px;
    padding: 100px 0 0 100px;

    .title {
      font-weight: bold;
      font-size: 30px;
    }

    .detail {
      font-size: 15px;
    }
  }
`;
