import { ConstructionOutlined } from "@mui/icons-material";
import styled from "styled-components";

export const MovieCard = ({ list }) => {
  return (
    <MovieCardContainer>
      <div className="movie_thumnail">
        <img
          src={`http://image.tmdb.org/t/p/w500/` + list.poster_path}
          alt="영화 썸네일"
        />
      </div>
      <div className="movie_info">
        <div className="wrapper">
          <span className="movie_title">{list.title}</span>
          <div className="rating_container">
            <span className="movie_rating"> ⭐️ {list.vote_average}</span>
          </div>
        </div>
      </div>
    </MovieCardContainer>
  );
};

export const MovieCardContainer = styled.div`
  width: 250px;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  border-radius: 15px;
  background-color: #1e3269;
  span {
    color: #fff;
    font-size: 14px;
    font-weight: 700;
  }
  .movie_thumnail {
    img {
      border-radius: 15px 15px 0 0;
      width: 250px;
    }
  }
  .movie_info {
    width: 250px;
    border-radius: 0 0 15px 15px;
    background-color: #1e3269;
    height: 80px;
    .wrapper {
      display: flex;
      align-items: center;
      justify-content: space-between;
      margin-top: 5px;
    }
    span {
      padding: 5px;
      /* text-wrap: nowrap; */
    }
  }
`;
