import { Fragment } from "react";
import styled from "styled-components";
import { useForm } from "react-hook-form";
import React, { useRef, useState } from "react";

interface FormValue {
  name: string;
  email: string;
  age: int;
  password: string;
  pwCon: string;
}

export const SignupPage = (props) => {
  const {
    register,
    handleSubmit,
    formState: { errors },
    getValues,
  } = useForm < FormValue > { mode: "onSubmit" };

  return (
    <form onSubmit={handleSubmit(props.onSubmit)}>
      <div>회원가입 페이지</div>
      <div className="inputDiv">
        <span>
          <input
            type="text"
            className="name"
            placeholder="이름을 입력해주세요."
            {...register("name", {
              required: "이름을 입력해주세요!",
            })}
          />
          {errors.name && <small role="alert">{errors.name.message}</small>}
        </span>
        <span>
          <input
            type="text"
            className="emailVal"
            placeholder="이메일을 입력해주세요."
          />
        </span>
        <span className="emailMes"></span>
        <span>
          <input
            type="text"
            className="ageVal"
            placeholder="나이를 입력해주세요."
          />
        </span>
        <span className="ageMes"></span>
        <span>
          <input
            type="text"
            className="passVal1"
            placeholder="비밀번호를 입력해주세요."
          />
        </span>
        <span className="passMes1"></span>
        <span>
          <input type="text" className="passVal2" placeholder="비밀번호 확인" />
        </span>
        <span className="passMes2"></span>
      </div>
      <div>
        <button>제출하기</button>
      </div>
      <div className="movePage">
        <span>이미 아이디가 있으신가요?</span>
        <span>로그인 페이지로 이동하기</span>
      </div>
    </form>
  );
};
