import styled from "styled-components";
import { NavLink } from "react-router-dom";

export const NavBar = () => {
  return (
    <NavWrapper>
      <NavLink to="/" className="logo">
        UMC Movie
      </NavLink>
      <div className="linkContainer">
        <span className="link">회원가입</span>
        <NavLink to="/popular" className="link">
          Popular
        </NavLink>
        <NavLink to="/nowplaying" className="link">
          Now Playing
        </NavLink>
        <NavLink to="/toprated" className="link">
          Top Rated
        </NavLink>
        <NavLink to="/upcoming" className="link">
          Upcoming
        </NavLink>
      </div>
    </NavWrapper>
  );
};

const NavWrapper = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 10px;
  height: 30px;
  border: 1px solid #fff;

  .logo {
    font-weight: 600;
    font-size: 18px;
  }

  .link {
    text-decoration: none;
    &:hover {
      cursor: pointer;
      color: #e4b940;
      font-weight: 700;
    }
  }

  .linkContainer {
    display: flex;
    gap: 20px;
  }
`;
