import styled from "styled-components";
import imageIcon from "../search.png";

export const Main = () => {
  return (
    <MainWrapper>
      <div className="welcome">ÌôòÏòÅÌï©ÎãàÎã§</div>
      <div className="search">
        <span> üìΩÔ∏è Find your movies !</span>
        <div className="searchInputContainer">
          <input type="text" className="searchInput" />
          <button type="button" className="btn">
            <img src={imageIcon} className="btnImage" />
          </button>
        </div>
      </div>
    </MainWrapper>
  );
};

export const MainWrapper = styled.div`
  width: 100%;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;

  .welcome {
    background-color: black;
    width: 100%;
    height: 365px;
    display: flex;
    justify-content: center;
    align-items: center;
    font-size: 24px;
    font-weight: 400;
  }
  .search {
    width: 100%;
    /* padding: 50px 0 250px 0; */
    font-size: 34px;
    /* text-align: center; */
    border: 1px solid;
    background-color: bisque;
    span {
      border: 1px solid;
    }
    .searchInputContainer {
      /* padding-top: 30px; */
      border: 1px solid;
      /* display: flex;
      align-items: center;
      justify-content: center; */
      margin-top: 30px;
    }

    .searchInput {
      width: 350px;
      height: 30px;
      /* -webkit-border-radius: 20px;
      -moz-border-radius: 20px; */
      border-radius: 20px;
    }

    .btn {
      position: relative;
      background: none;
      border: none;
      margin-left: 30px;
    }

    .btnImage {
      position: absolute;
      transform: translate(-50%, -50%);
      /* ÌïÑÏöîÏóê Îî∞Îùº Ïù¥ÎØ∏ÏßÄ ÌÅ¨Í∏∞ Ï°∞Ï†à */
      width: 30px;
      height: 30px;
    }
  }
`;
