import styled from "styled-components";
import { Link } from "react-router-dom";
import { useState } from "react";

export const NavBar = () => {
  const [page, setPage] = useState("logo");
  const [isLogin, setIsLogin] = useState(false);

  // 페이지 상태 핸들러
  const handlerPageRouter = (value) => {
    setPage(value);
  };

  // 로그인, 로그아웃 핸들러
  const handlerLogin = () => {
    setIsLogin(!isLogin);
    // if (isLogin === true) {
    //   setIsLogin(false);
    // } else {
    //   setIsLogin(true);
    // }
  };
  return (
    <NavWrapper>
      <NavLink
        to="/"
        className={`${page === "logo" && "link"}`}
        onClick={() => handlerPageRouter("logo")}
      >
        UMC Movie
      </NavLink>
      <div className="linkContainer">
        {/* 로그인 로그아웃에 대한 조건 처리 => 삼항연산자 */}
        {isLogin ? (
          <span className="login" onClick={handlerLogin}>
            로그아웃
          </span>
        ) : (
          <span className="login" onClick={handlerLogin}>
            로그인
          </span>
        )}

        <NavLink
          to="/popular"
          className={`${page === "popular" ? "active" : "link"}`}
          onClick={() => handlerPageRouter("popular")}
        >
          Popular
        </NavLink>
        <NavLink
          to="/nowplaying"
          className={`${page === "nowplaying" ? "active" : "link"}`}
          onClick={() => handlerPageRouter("nowplaying")}
        >
          Now Playing
        </NavLink>
        <NavLink
          to="/toprated"
          className={`${page === "toprated" ? "active" : "link"}`}
          onClick={() => handlerPageRouter("toprated")}
        >
          Top Rated
        </NavLink>
        <NavLink
          to="/upcoming"
          className={`${page === "upcoming" ? "active" : "link"}`}
          onClick={() => handlerPageRouter("upcoming")}
        >
          Upcoming
        </NavLink>
      </div>
    </NavWrapper>
  );
};

const NavWrapper = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 10px;
  height: 30px;
  background-color: #000020;
  position: fixed;
  top: 0;
  width: -webkit-fill-available;
  .logo {
    font-weight: 600;
    font-size: 18px;
  }

  .login {
    color: #e4b940;
    font-weight: 700;
  }

  .link,
  .login {
    &:hover {
      cursor: pointer;
      color: #e4b940;
      font-weight: 700;
    }
  }

  .active {
    text-decoration: none;
    color: #e4b940;
    font-weight: 700;
  }

  .linkContainer {
    display: flex;
    gap: 20px;
  }
`;

const NavLink = styled(Link)`
  text-decoration: none;
  color: white;
`;
