import { Children, Fragment, useEffect, useState } from "react";
import { Layout } from "../components/Layout";
import { MovieCard } from "../components/MovieCard";

export const PopularPage = () => {
  const [dataList, setDataList] = useState([]);
  const [loading, setLoading] = useState(false);
  const [currentPage, setCurrentPage] = useState(1);
  console.log(loading);

  const options = {
    method: "GET",
    headers: {
      accept: "application/json",
      Authorization:
        "Bearer eyJhbGciOiJIUzI1NiJ9.eyJhdWQiOiIzNjhlZTc5NDRlYTBhNWM3Zjk0NmZmY2Q1ZGJmZGZkNCIsInN1YiI6IjY2MzhkYWNjNjY1NjVhMDEyODE2OTgxNyIsInNjb3BlcyI6WyJhcGlfcmVhZCJdLCJ2ZXJzaW9uIjoxfQ.qqf1m6FqRpLf-xacc6WhD8OAA6J6QGDvtLRW8AdWEVg",
    },
  };

  useEffect(() => {
    setLoading(true);

    const fetchData = async () => {
      const data = await fetch(
        "https://api.themoviedb.org/3/movie/popular?language=en-US&page=1",
        options
      )
        .then((response) => response.json())
        .catch((err) => console.error(err));
      // console.log(data);
      setDataList(data.results);
      setLoading(false);
    };

    fetchData();
  }, []);

  const handleNextPage = () => {
    setCurrentPage((prevPage) => prevPage + 1);
  };

  const handlePrevPage = () => {
    if (currentPage > 1) {
      setCurrentPage((prevPage) => prevPage - 1);
    }
  };
  return (
    <Fragment>
      <Layout>
        {Children.toArray(
          dataList.map((index) => {
            return <MovieCard list={index} />;
          })
        )}
      </Layout>
      <div className="pagemove">
        <h3 onClick={handlePrevPage}>{"<"}</h3>
        <h3>{currentPage}</h3>
        <h3 onClick={handleNextPage}>{">"}</h3>
      </div>
    </Fragment>
  );
};
