import styled from "styled-components";
import SearchIcon from "@mui/icons-material/Search";
import { Children, useEffect, useState } from "react";
import MovieCard from "../components/MovieCard";

export const Main = () => {
  const [dataList, setDataList] = useState([]);
  console.log(loading);
  const options = {
    method: "GET",
    headers: {
      accept: "application/json",
      Authorization:
        "Bearer eyJhbGciOiJIUzI1NiJ9.eyJhdWQiOiIzNjhlZTc5NDRlYTBhNWM3Zjk0NmZmY2Q1ZGJmZGZkNCIsInN1YiI6IjY2MzhkYWNjNjY1NjVhMDEyODE2OTgxNyIsInNjb3BlcyI6WyJhcGlfcmVhZCJdLCJ2ZXJzaW9uIjoxfQ.qqf1m6FqRpLf-xacc6WhD8OAA6J6QGDvtLRW8AdWEVg",
    },
  };

  useEffect(() => {
    setLoading(true);

    const fetchData = async () => {
      const data = await fetch(
        "https://api.themoviedb.org/3/search/movie?include_adult=false&language=en-US&page=1",
        options
      )
        .then((response) => response.json())
        .catch((err) => console.error(err));

      setDataList(data.results);
    };

    fetchData();
  }, []);

  return (
    <MainWrapper>
      <div className="welcome">ÌôòÏòÅÌï©ÎãàÎã§</div>
      <div className="search">
        <span> üìΩÔ∏è Find your movies !</span>
        <div className="searchInputContainer">
          <form onSubmit={handleSearch}>
            <input
              type="text"
              className="searchInput"
              onChange={(e) => setSearch(e.target.value)}
            />
            <button type="button" className="btn">
              <SearchIcon className="search_icon" />
            </button>
          </form>
        </div>
        <div className="movies">
          {Children.toArray(
            dataList.map((index) => {
              return <MovieCard list={index} />;
            })
          )}
        </div>
      </div>
    </MainWrapper>
  );
};

export const MainWrapper = styled.div`
  width: 100%;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  .welcome {
    background-color: black;
    width: 100%;
    height: 365px;
    display: flex;
    justify-content: center;
    align-items: center;
    font-size: 40px;
    font-weight: 400;
  }

  .search {
    width: 100%;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    gap: 50px;
    margin-top: 50px;
    span {
      font-size: 44px;
      padding-right: 70px;
    }
  }

  .searchInputContainer {
    display: flex;
    align-items: center;
    justify-content: center;
    gap: 30px;
    width: 100%;
    height: 50px;
    input {
      border-radius: 30px;
      border: none;
      width: 400px;
      height: 40px;
    }
    button {
      border-radius: 50%;
      width: 35px;
      height: 35px;
      background-color: orange;
      border: none;
    }
  }
`;
